CREATE TABLE `framework_users` (`id` INT NOT NULL AUTO_INCREMENT , `username` TEXT NOT NULL , `first_name` TEXT NOT NULL , `last_name` TEXT NOT NULL , `email` TEXT NOT NULL , `password` TEXT NOT NULL , `avatar` TEXT NULL DEFAULT 'https://www.gravatar.com/avatar' , `uuid` TEXT NOT NULL , `token` TEXT NOT NULL , `role` ENUM('Administrator','Admin','Support','Member') NOT NULL , `first_ip` TEXT NOT NULL , `last_ip` TEXT NOT NULL , `banned` TEXT NULL DEFAULT NULL , `last_seen` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP , `first_seen` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB;