CREATE TABLE `framework_admin_apikeys` (`id` INT NOT NULL AUTO_INCREMENT , `name` TEXT NOT NULL , `type` ENUM('r','rw') NOT NULL DEFAULT 'r' , `value` TEXT NOT NULL , `allowed_ips` TEXT NOT NULL DEFAULT '%' , `deleted` ENUM('false','true') NOT NULL DEFAULT 'false' , `locked` ENUM('false','true') NOT NULL DEFAULT 'false' , `date` DATETIME NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB;