CREATE TABLE `framework_backups` (`id` INT NOT NULL AUTO_INCREMENT , `backup_name` TEXT NOT NULL , `backup_type` ENUM('database','app','all') NOT NULL , `backup_path` TEXT NOT NULL , `backup_status` ENUM('IN_PROGRESS','DONE','FAILED') NOT NULL DEFAULT 'IN_PROGRESS' , `backup_date_start` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP , `backup_date_end` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB;