CREATE TABLE `framework_user_mails` (`id` INT NOT NULL AUTO_INCREMENT , `subject` TEXT NOT NULL , `body` LONGTEXT NOT NULL , `from` TEXT NOT NULL DEFAULT 'app@mythicalsystems.xyz' , `uuid` TEXT NOT NULL , `date` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB;
ALTER TABLE `framework_user_mails` ADD `read` INT NOT NULL DEFAULT '0' AFTER `uuid`, ADD `deleted` ENUM('false','true') NOT NULL DEFAULT 'false' AFTER `read`, ADD `locked` ENUM('false','true') NOT NULL DEFAULT 'false' AFTER `deleted`;
