CREATE TABLE `framework_users_social_likes` (`id` INT NOT NULL AUTO_INCREMENT , `uuid_from` TEXT NOT NULL , `uuid_to` TEXT NOT NULL , `interaction` ENUM('like','dislike') NOT NULL DEFAULT 'like' , `deleted` ENUM('false','true') NOT NULL DEFAULT 'false' , `locked` ENUM('false','true') NOT NULL DEFAULT 'false' , `date` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB;